import { View, Text, TouchableOpacity } from 'react-native'
import React, { useState } from 'react'
import { Image } from 'expo-image';
import { useDispatch } from 'react-redux';
import { setSong } from '../features/searchSlice';
import { useNavigation } from '@react-navigation/native';
import { setPlaylist } from '../features/playlistSlice';


const SearchedSong = ({id, playlistId, thumbnail, title, uploaderName, duration, uploaderUrl}) => {
    const dispatch = useDispatch()
    const [searchResults, setSearchResults] = useState()



    const navigation = useNavigation()


    const handleSong = async() => {
      console.log(playlistId, id)
        dispatch(setSong({
            id,
            playlistId,
            title,
            thumbnail,
            uploaderName,
            uploaderUrl,
            duration,
          }))
          const url = `https://beatbump.io/api/v1/next.json?playlistId=${playlistId}&configType=MUSIC_VIDEO_TYPE_ATV`;
    console.log(url);
    await fetch(url, {
        method: 'GET',
        headers: {
            'Content-Type': 'text/plain',
            "Connection": "keep-alive",
            "User-Agent": "MY-UA-STRING"
        }
    })
    .then(res => res.json())
    .then((apiResponse) => {
        const formattedResults = apiResponse.results.map(item => ({
            id: item.videoId,
            playlistId: item.playlistId,
            thumbnail: item.thumbnails[0].url,
            title: item.title,
            uploaderName: item.artistInfo.artist[0].text,
            // duration: item.subtitle[4].text
        }));
        setSearchResults(formattedResults);

        })
        .catch((error) => {
            console.error('Error fetching search results:', error);
        });
        {searchResults?.map((song) => (
        dispatch(setSong({
          id: song.id,
          playlistId: song.playlistId,
          title: song.title,
          thumbnail: song.thumbnail,
          uploaderName: song.uploaderName,
          uploaderUrl: song.uploaderUrl,
          duration: song.duration,
        }))
        ))}
          navigation.navigate('song')

  }


  return (
    <View>
        <TouchableOpacity onPress={handleSong}>
         <View style={{flexDirection: "row", alignItems: "center", marginTop: 20}}>

    <Image style={{ width: 70, height: 70, marginRight: 10 }} source={{uri: thumbnail}}/>
    <View style={{flexDirection: "column"}}>
        <View style={{flexDirection: "row", alignItems: "center"}}>
        <Text style={{fontSize: 15}}>
        {title}

      </Text>
      <Text style={{marginLeft: 20}}>
            {duration}
        </Text>
        </View>

        <Text style={{fontSize: 12}}>
            {uploaderName}
        </Text>
        </View>
      </View>
      </TouchableOpacity>
    </View>
  )
}

export default SearchedSong
